// Color Variables

$color-primary: #d4af37;
$color-primary-light: #ead79b;
$color-primary-dark: #aa8c2c;
$color-secondary: #3d3d3d;
$color-secondary-light: #8b8b8b;
$color-secondary-dark: #252525;
$color-tertiary: #1b2a4b;
$color-tertiary-light: #5f6a81;
$color-tertiary-dark: #131d35;

$color-font-secondary: #252525;

//Font Variables

$font-primary: "Playfair Display", serif;
$font-secondary: "Lato", sans-serif;

// screen sizes

$bp-largest: 75em; // 1200px
$bp-large: 62.5em; // 1000px
$bp-medium: 50em; // 800px;
$bp-small: 37.5em; // 600px;

*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
  scroll-behavior: smooth;

  @media only screen and (max-width: $bp-largest) {
    font-size: 50%;
  }

  @media only screen and (max-width: $bp-small) {
    font-size: 40%;
  }
}

body {
  font-family: $font-primary;
  color: $color-secondary-dark;
  font-weight: 400;
  line-height: 1.8;
  font-size: 1.4rem;
  background-color: rgba($color-secondary-light, 0.1);
}

.container {
  display: grid;
  grid-template-rows: 95vh max-content max-content repeat(3, min-content);
  grid-template-columns:
    [sidebar-start] 8rem [sidebar-end full-start] minmax(6rem, 1fr)
    [center-start] repeat(8, [col-start] minmax(min-content, 14rem) [col-end])
    [center-end]minmax(6rem, 1fr) [full-end];

  @media only screen and (max-width: $bp-medium) {
    grid-template-rows: 6rem 95vh max-content max-content repeat(3, min-content);
    grid-template-columns:
      [full-start] minmax(6rem, 1fr)
      [center-start] repeat(8, [col-start] minmax(min-content, 14rem) [col-end])
      [center-end]minmax(6rem, 1fr) [full-end];
  }
}

.btn {
  background-color: $color-primary;
  color: #fff;
  border: none;
  padding: 1rem 2rem;
  font-family: $font-primary;
  font-size: 2rem;
  font-weight: 500;

  &:active,
  &:hover {
    background-color: #fff;
    outline: 3px solid $color-tertiary-dark;
    color: $color-secondary-dark;
  }
}

sources.scss\\_header.scss:
.header {
  grid-column: full-start/ full-end;
  background-image: linear-gradient(
      rgba($color-secondary, 0.6),
      rgba($color-secondary, 0.6)
    ),
    url(../img/art-1.jpg);
  background-size: cover;
  background-position: center;
  padding: 4rem;
  padding-top: 0;

  display: grid;
  grid-template-rows: 1fr min-content 6rem 1fr;
  grid-template-columns: max-content;
  row-gap: 1.5rem;
  justify-content: center;

  &__logo-link {
    justify-self: center;
    align-content: center;
  }

  &__logo {
    height: 10rem;
  }

  &__seenon-text {
    display: grid;
    grid-template-columns: 1fr max-content 1fr;
    column-gap: 1.5rem;
    align-items: center;
    font-size: 1.8rem;
    color: #999;

    &::before,
    &::after {
      content: "";
      height: 1px;
      display: block;
      background-color: currentColor;
    }
  }

  &__seenon-logos {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 2rem;
    justify-content: center;

    &-1,
    &-4 {
      filter: brightness(70%);
    }

    img {
      height: 25%;
      justify-self: center;

      @media only screen and (max-width: $bp-medium) {
        height: 15%;
      }
    }
  }

  &__btn {
    align-self: start;
    justify-self: start;
    margin-top: 2.5rem;
  }
}

sources.scss\\_sidebar.scss:
.sidebar {
  background-color: $color-primary;
  grid-column: sidebar-start/ sidebar-end;
  grid-row: 1/-1;

  @media only screen and (max-width: $bp-medium) {
    grid-column: 1/-1;
    grid-row: 1/2;
  }
}

.navigation {
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 1500;

  &__nav {
    display: flex;
  }
  &__link {
    &:link,
    &:visited {
      font-size: 3rem;
      color: #fff;
      padding: 0.5rem 1rem;
      text-decoration: none;

      background-image: linear-gradient(
        120deg,
        transparent 0%,
        transparent 50%,
        $color-tertiary-light 50%
      );
      background-size: 230%;
      transition: all 0.2s;
    }

    &:hover,
    &:active {
      background-position: 100%;
    }
  }

  &__list {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    position: absolute;
    left: -100%;
    height: 100vh;
    background-image: linear-gradient(
      to bottom right,
      $color-primary-light,
      $color-primary-dark
    );
    width: 100%;
    transition: all 0.3s;
    opacity: 98%;
    z-index: 200;

    &.active {
      left: 0;
    }
  }

  &__item {
    margin: 1.6rem 0;
    list-style: none;
  }

  &__hamburger {
    @media only screen and (max-width: $bp-medium) {
      background-color: rgba($color-primary, 0.8);
      height: 5rem;
      width: 5rem;
      border-radius: 50%;
      position: fixed;
      top: 1.05rem;
      left: 88.75%;
      z-index: 800;
    }

    &__bars {
      cursor: pointer;
      display: block;
      position: fixed;
      top: 3rem;
      left: 2.5rem;
      z-index: 700;

      @media only screen and (max-width: $bp-medium) {
        top: 2rem;
        left: 90%;
      }
    }

    &.active .bar:nth-child(2) {
      opacity: 0;
    }

    &.active .bar:nth-child(1) {
      transform: translateY(0.8rem) rotate(45deg);
    }

    &.active .bar:nth-child(3) {
      transform: translateY(-0.8rem) rotate(-45deg);
    }
  }

  .bar {
    display: block;
    width: 3rem;
    height: 0.3rem;
    margin: 0.5rem 0;
    transform: all 0.3 ease;
    background-color: #fff;
  }
}

