.footer {
  background-color: $color-secondary-dark;
  grid-column: full-start/ full-end;
  display: flex;
  gap: 2rem;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  &__logo {
    width: 10rem;
    height: 10rem;
  }

  &__nav {
    display: flex;
    gap: 7rem;

