    &__link:link,
    &__link:visited {
      font-size: 1.8rem;
      color: #fff;
      font-family: $font-secondary;
      text-decoration: none;
      text-align: center;

      @media only screen and (max-width: $bp-medium) {
        font-size: 1.4rem;
      }
    }
  }
}

.copyright {
  color: rgba(#fff, 0.5);
}

