.testimonial {
  grid-column: full-start/ full-end;
  padding: 7rem 0;
  background-image: linear-gradient(
    to right bottom,
    $color-tertiary-light,
    $color-tertiary-dark
  );
  display: flex;
  justify-content: center;
  align-items: center;

  &__content {
    background-image: linear-gradient(
        105deg,
        rgba($color-primary, 0.95) 0%,
        rgba($color-primary, 0.95) 50%,
        transparent 50%
      ),
      url(../img/art-testimonial.jpg);
    background-size: cover;
    background-position: center;
    // height: 70vh;
    width: 80vw;

    @media only screen and (max-width: $bp-largest) {
      background-image: linear-gradient(
          rgba($color-primary, 0.9),
          rgba($color-primary, 0.9)
        ),
        url(../img/art-testimonial.jpg);
      background-size: cover;
    }
  }

  &__information {
    display: flex;
    flex-direction: column;

    justify-content: center;
    align-items: center;

    width: 50%;
    padding: 0 3rem;

    @media only screen and (max-width: $bp-largest) {
      width: 100%;
    }
  }

  &__heading {
    color: $color-font-secondary !important;
  }

  &__user-img {
    width: 15rem;
    height: 15rem;
    border-radius: 50%;
    margin-bottom: 2rem;
  }

  & p {
    font-size: 1.8rem;
  }
}

sources.scss\\_topsellers.scss:
.topsellers {
  grid-column: center-start/ center-end;
  padding-bottom: 7rem;

  &__items {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(25rem, 1fr));
    gap: 6rem;
  }
}

.topseller {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  background-color: rgba($color-secondary-light, 0.2);
  row-gap: 3.5rem;

  &__img {
    width: 100%;
    grid-column: 1/-1;
    grid-row: 1/2;
  }

  &__name {
    width: 80%;
    justify-self: center;
    align-self: end;

    grid-column: 1/-1;
    grid-row: 1/2;
    z-index: 3;
    font-size: 1.6rem;
    text-align: center;
    padding: 1.25rem;
    background-color: $color-tertiary;
    color: #fff;
    font-weight: 400;
    transform: translateY(50%);
  }

  &__location,
  &__artist {
    margin-top: 2.5rem;
  }
  &__location,
  &__artist,
  &__size,
  &__price {
    font-size: 1.5rem;
    margin-left: 2rem;
    display: flex;
    align-items: center;
    gap: 1rem;

    svg {
      fill: $color-tertiary;
      height: 2rem;
      width: 2rem;
    }
  }

  &__btn {
    grid-column: 1/-1;
  }
}
.topsellers h3 {
  color: $color-font-secondary;
  text-align: center;
}

